require_relative 'keypad'

RSpec.describe Keypad do
  describe 'part one' do
    it 'works simply' do
      data = "ULL\nRRDDD\nLURDL\nUUUUD"
      code = Keypad.parse(data)
      expect(code).to eq '1985'
    end

    it 'solves the clue' do
      data = '



'
      code = Keypad.parse(data)
      expect(code).to eq '47978'
    end
  end

  describe 'part two' do
    it 'works simply' do
      data = "ULL\nRRDDD\nLURDL\nUUUUD"
      code = Keypad.parse(data, :alphanum)
      expect(code).to eq '5DB3'
    end

    it 'solves the clue' do
      data = '



'
      code = Keypad.parse(data, :alphanum)
      expect(code).to eq '659AD'
    end
  end
end
